contract BadCacheHolder is ERC721Holder {
  function onERC721Received(
    address _sender,
    address _receiver,
    uint256 _tokenId,
    bytes memory _data
  ) public override returns (bytes4) {
    console.logString("Herererere");
    console.log(_sender);
    return super.onERC721Received(_sender, _receiver, _tokenId, _data);
  }
}

